import React, {useEffect, useState} from 'react';
import { customStyles } from '../../helpers/center-modal-styles';
import Modal from 'react-modal';
import DateTimePicker from 'react-datetime-picker';
import moment from 'moment';
import Swal from 'sweetalert2';
import { useDispatch, useSelector } from 'react-redux';
import { uiCloseModal } from '../../actions/ui';
import { eventAddNew, eventClearActiveEvent } from '../../actions/calendar';

Modal.setAppElement('#root');

const now = moment().minutes(0).seconds(0).add(1, 'hours');
const endDate = now.clone().add(1, 'hours');

const initialState ={
    title: '',
    notes : '',
    start: now.toDate(),
    end: endDate.toDate()
};

export const CalendarModal = () => {

    const ref = useRef(true);
    const {modalOpen} = useSelector(state => state.ui);
    const {activeEvent} = useSelector(state => state.calendar);
    const dispatch = useDispatch();
    const [titleValid, setTitleValid] = useState(true);
    const [dateStart, setDateStart] = useState(now.toDate());
    const [dateEnd, setDateEnd] = useState(endDate.toDate());

    const [formValues, setFormValues] = useState(initialState);

    const {title, notes, start, end} = formValues;

    useEffect(() => {
        return () => {
            ref.current = false;
        }
    }, [])

    useEffect(() => {
        if(activeEvent && ref){
            setFormValues(activeEvent);
        }
    }, [activeEvent, setFormValues, ])

    const handleInputChange = ({target}) =>{
        setFormValues({...formValues,
                        [target.name]: target.value});
    }

    const closeModal = () =>{
        dispatch(uiCloseModal());
        dispatch(eventClearActiveEvent());
        setFormValues(initialState);
    }

    const onChangeStartDate = (e) =>{
        setDateStart(e);
        setFormValues({...formValues,
                        start: e})
    }

    const onChangeEndDate = (e) =>{
        setDateEnd(e);
        setFormValues({...formValues,
            end: e})
    }

    const handleSubmitForm = (e) =>{
        e.preventDefault();
        
        const momentStart = moment(start);
        const momentEnd = moment(end);

        if( momentStart.isSameOrAfter(momentEnd)){
           return Swal.fire('Error', 'La fecha fin debe de ser mayor a la fecha de inicio', 'error');  
        }
        if(!/^[a-zA-Z]\w+(\s+\w+)?$/.test(title)){
            return setTitleValid(false);
        }
        dispatch(eventAddNew({
            ...formValues,
            id: new Date().getTime(),
            user: {
                _id: '123',
                name: 'Branco'
            }
        }))
        setTitleValid(true);
        closeModal();
    }

    return (
        <Modal
            isOpen={modalOpen}
            // onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={customStyles}
            closeTimeoutMS={200}
            className='modal'
            overlayClassName='modal-fondo'
        >
            <h1> Nuevo evento </h1>
            <hr />
            <form onSubmit={handleSubmitForm} className="container">

                <div className="form-group">
                    <label>Fecha y hora inicio</label>
                    <DateTimePicker
                        onChange = {onChangeStartDate}
                        value = {dateStart}
                        className= 'form-control'
                    />
                </div>

                <div className="form-group">
                    <label>Fecha y hora fin</label>
                    <DateTimePicker
                        onChange = {onChangeEndDate}
                        value = {dateEnd}
                        minDate={dateStart}
                        className= 'form-control'
                    />
                </div>

                <hr />
                <div className="form-group">
                    <label>Titulo y notas</label>
                    <input 
                        type="text" 
                        className={`form-control ${(titleValid)?'is-valid':''}`}
                        placeholder="Título del evento"
                        name="title"
                        autoComplete="off"
                        value={title}
                        onChange = {handleInputChange}
                    />
                    <small id="emailHelp" className="form-text text-muted">Una descripción corta</small>
                </div>

                <div className="form-group">
                    <textarea 
                        type="text" 
                        className="form-control"
                        placeholder="Notas"
                        rows="5"
                        name="notes"
                        value={notes}
                        onChange = {handleInputChange}
                    ></textarea>
                    <small id="emailHelp" className="form-text text-muted">Información adicional</small>
                </div>

                <button
                    type="submit"
                    className="btn btn-outline-primary w-100"
                >
                    <i className="far fa-save"></i>
                    <span> Guardar</span>
                </button>

            </form>
        </Modal>
    )
}
